INSERT INTO `film` (`idfilm`, `titolo`, `regista`, `genere`, `durata`, `nazione`, `anno`, `descrizione`, `trailer`, `locandina`, `lingua`, `attori`, `attivo` ) VALUES
( NULL, 'Killers of the Flower Moon', 'Martin Scorsese', 'Drammatico', 205, 'USA', 2023, 'Oklahoma, primi anni Venti. Ernest Burkhart ha combattuto in guerra e torna nella nativa Fairfax in cerca di fortuna.', 'https://youtu.be/7cx9nCHsemc?si=FqLtGfXZT5f4-YYA','killers-of-the-flower-moon.png', 'Ita', 'Leonardo Di Caprio, Robert De Niro, Jesse Plemons', 1),
( NULL, 'Hunger Games: la ballata dell''usignolo e del serpente', 'Francis Lawrence', 'Azione, Avventura', 165, 'USA', 2023, 'Anni prima di diventare il tirannico presidente di Panem, il diciottenne Coriolanus Snow è l ultima speranza per il buon nome della sua casata in declino: un orgogliosa famiglia caduta in disgrazia nel dopoguerra di Capitol City.','https://www.youtube.com/embed/kqAyrCadrQU?fs=1&autoplay=1', 'The-Hunger-Games-the-Ballad-of-Songbirds-and-Snakes.png','Ita','Rachel Zegler, Tom Blyth, Hunter Schafer', 1),
( NULL, 'Gremlins', 'Joe Dante', 'Fantastico', 111, 'USA', 1984, 'Rand Peltzer, inventore, ha trovato un regalo natalizio perfetto per suo figlio Bill: un piccolo mogwai tenero e buffo. ','https://www.youtube.com/embed/rKpl61np8aE?fs=1&autoplay=1', 'gremlins.png','Ita','Phoebe Cates, Keye Luke, Zach Galligan', 1),
( NULL, 'The Marvels', 'Nia DaCosta', 'Azione', 101, 'USA', 2023, 'Carol Danvers alias Captain Marvel ha recuperato la propria identità dai tirannici Kree e si è vendicata della Suprema Intelligenza. Ma a causa di conseguenze impreviste, Carol deve farsi carico del peso di un universo destabilizzato.','https://www.youtube.com/embed/nrMX5IcQZpE?fs=1&autoplay=1','the-marvels.png','Ita','Brie Larson, Teyonah Parris, Zawe Ashton', 1),
( NULL, 'The Nun II', 'Michael Chaves', 'Horror', 109, 'USA', 2023, '1956 - Francia. Un prete viene assassinato. Un male si sta diffondendo. Il sequel del film campione d''incassi segue le vicende di Suor Irene, quando viene a trovarsi nuovamente faccia a faccia con Valak, la suora demoniaca.','https://youtu.be/QF-oyCwaArU', 'the-nun-2.png','Ita','Bonnie Aarons, Taissa Farmiga, Anna Popplewell', 1),
( NULL, 'Oppenheimer', 'Christopher Nolan', 'Biografico, Drammatico, Storico', 180, 'USA', 2023, 'Basato sulla biografia Robert Oppenheimer, il padre della bomba atomica di Kai Bird e Martin J. Sherwin, il film racconta la vita del fisico teorico statunitense J. Robert Oppenheimer.','https://youtu.be/bK6ldnjE3Y0', 'oppenheimer.png', 'Ita','Cillian Murphy, Emily Blunt, Robert Downey Jr., Matt Damon, Rami Malek', 0),
( NULL, 'Povere Creature!', 'Yorgos Lanthimos', 'Fantascienza', 101, 'USA', 2023, 'Oltre alle cicatrici che lo sfigurano e alle terribili menomazioni del suo fisico, Godwin Baxter deve a suo padre anche una sincera passione per il metodo scientifico e le pratiche chirurgiche.','https://youtu.be/RlbR5N6veqw','poor-things.png', 'Ita','Emma Stone, Margaret Qualley, Willem Dafoe, Mark Ruffalo', 0);
( NULL, 'Garfield: Una Missione Gustosa', 'Mark Dindal', 'animazione', NULL, NULL, '2024', 'In questa nuova avventura, dopo un inaspettato incontro con il padre perduto da tempo, il trasandato gatto di strada Vic, Garfield e il suo amico canino Odie sono costretti a lasciare la loro vita piena di comodità per unirsi a Vic in una rapina ad alto rischio.', 'https://youtu.be/FiTUp0SU1f0?si=ZBhh0CJk_p9_V0WZ', 'Garfield.png', NULL, NULL, 2),
( NULL, 'Mean Girls', 'Arturo Perez Jr. and Samantha Jayne', 'commedia, musicale', NULL, NULL, '2024', 'Cady Heron riesce a integrarsi nel suo nuovo liceo ed entra a far parte della cricca di ragazze di prima qualità, le Plastics. Poi, però, commette il tragico errore di innamorarsi di Aaron Samuels, ex fidanzato di Regina George.', 'https://youtu.be/fFtdbEgnUOk?si=L6MItsJwSYWo6ZxJ', 'mean-girls.png', NULL, NULL, 2),
( NULL, 'Argylle', 'Matthew Vaughn' ,'spionaggio, azione', NULL, NULL, '2024', 'Elly Conway, una solitaria scrittrice di best-seller di spionaggio, gradisce passare le serate davanti al computer in compagnia del suo gatto Alfie. Si ritroverà catapultata nel vero mondo dello spionaggio quando le trame dei suoi libri, incentrati su Argylle, un agente segreto, inizieranno a diventare reali. Le vicende raccontate nei suoi romanzi sembreranno essere troppo simili alle attività svolte da una organizzazione segreta di spionaggio. Quando la spia Aiden le si presenta per salvarla da un rapimento, Elly e Alfie si ritroveranno in un mondo dove nulla è come sembra.', 'https://youtu.be/7mgu9mNZ8Hk?si=qBKDZ0Jbrle9wYeL', 'Argylle.png', NULL, NULL, 2),
( NULL, 'Arthur the King', 'Simon Cellan Jones', 'drama, azione', NULL, NULL, '2024', 'Un campione di raid - le cui gare prevedono un viaggio avventuroso con destinazione prefissata che comporta la percorrenza di una lunga distanza - adotta un cane randagio di nome Arthur, che si unisce a lui in una gara di resistenza.', 'https://youtu.be/wjDJNEPghNY?si=rNm9ubF8iudZ1W4B', 'arthur-the-king.png', NULL, NULL, 2),
( NULL, 'Joker: Folie à Deux', 'Todd Phillips', 'thriller psicologico musicale',NULL, NULL, '2024', 'Sequel di Joker; un musical ambientato principalmente all’Arkham Asylum e si concentrerà sul tempo trascorso da Joker nell’iconico manicomio criminale.', 'https://youtu.be/03ymBj144ng?si=64Qni2C8o1RFj_zF', 'joker-2.png', NULL, NULL, 2),
( NULL, 'Beetlejuice 2', 'Tim Burton', 'thriller, fantasy',NULL, NULL, '2024', NULL, 'https://youtu.be/llnuyh1wo8A?si=4c_ibI70GMIMhlRK', 'beetlejuice-2.png', NULL, NULL, 2);
